import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AutenticacaoService } from './services/autenticacao/autenticacao.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  isAuthenticated = false; // Propriedade usada para verificar autenticação
  isMenuCollapsed = true;
  title = 'frontembarcacao';

  constructor(private authService: AutenticacaoService, private router: Router) {}

  ngOnInit(): void {
    // Verifica o usuário ao iniciar a aplicação
    this.authService.getUser().subscribe(user => {
      this.isAuthenticated = !!user; // Converte a resposta para um booleano
    });

    // Se não houver usuário salvo, força o logout
    if (!localStorage.getItem('user')) {
      this.isAuthenticated = false;
      this.router.navigate(['/login/login']);
    }
  }

  logout() {
    this.authService.logout();
    this.isAuthenticated = false;
    this.router.navigate(['/login/login']);
  }

  toggleMenu() {
    this.isMenuCollapsed = !this.isMenuCollapsed;
  }
}
<nav *ngIf="usuarioLogado" class="navbar navbar-expand-lg navbar-dark bg-navy">
  <div class="container-fluid">
    <a class="navbar-brand" routerLink="inicio" routerLinkActive="active">PROA</a>

    <button class="navbar-toggler" type="button" (click)="toggleMenu()">
      <span class="navbar-toggler-icon"></span>
    </button>

    <div id="navbarNav" class="collapse navbar-collapse" [ngClass]="{'show': !isMenuCollapsed}">
      <ul class="navbar-nav me-auto">
        <li class="nav-item">
          <a class="nav-link" routerLink="cliente/listar-cliente" routerLinkActive="active">Clientes</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" routerLink="embarcacao/listarembarcacao" routerLinkActive="active">Embarcações</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" routerLink="anexos/anexos" routerLinkActive="active">Anexos</a>
        </li>

        <li hidden class="nav-item">
          <a class="nav-link" routerLink="servico-anexo/servico-anexo" routerLinkActive="active">Serviços</a>
        </li>
      </ul>
      <ul class="navbar-nav">
        <li class="nav-item">
          <a class="nav-link" routerLink="empresa/empresa" routerLinkActive="active">Opções</a>
        </li>
        <li class="nav-item">
          <button class="btn btn-outline-light" (click)="logout()">Logout</button>
        </li>
      </ul>
    </div>
  </div>
</nav>

<div class="content-wrapper">
  <router-outlet></router-outlet>
</div>

<footer *ngIf="usuarioLogado" class="footer bg-navy text-white">
  <div class="container text-center">
    <span class="footer-title">PROA</span>
    <p class="footer-description">Projeto de apoio a Regularização em Operações Aquaviárias</p>
  </div>
</footer>
