import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { Cliente } from 'src/app/model/cliente';
import { FrontClienteService } from 'src/app/services/front-cliente.service';
import { ValidadorcpfcnpjService } from 'src/app/services/validacao/validadorcpfcnpj.service';

@Component({
  selector: 'app-listar-cliente',
  templateUrl: './listar-cliente.component.html',
  styleUrls: ['./listar-cliente.component.css']
})
export class ListarClienteComponent implements OnInit {

  Clientes!: Cliente[];
  cpf: string = '';
  cliente!: Cliente | null;
  mensagemErro: string = '';

  constructor(private ClienteService: FrontClienteService, private router: Router, private serviceCpfCnpj: ValidadorcpfcnpjService) { }

  ngOnInit(): void { }



  consultarClientePorCPFCNPJ() {
    if (this.serviceCpfCnpj.validarCpfCnpj(this.cpf)) {
      const cpfcnpj = this.cpf.replace(/[^\d]+/g, '');
      this.ClienteService.consultarClienteCPFCNPJ(cpfcnpj).subscribe(cliente => {
        if (cliente) {
          this.cliente = cliente;
          this.mensagemErro = '';
        }
      }, error => {
        console.error(error);
        this.mensagemErro = 'Cliente não localizado!';
        this.cliente = null;
      });
    }else {
      //Exibe a mensagem de erro se o CPF/CNPJ for inválido
      this.mensagemErro = 'CPF ou CNPJ inválido!';
      this.cliente = null;
    }
  }

  alterarCliente(codigo: number) {
    this.router.navigate(['cliente/alterar-cliente', codigo]);
  }

  cadastrarCliente() {
    this.router.navigate(['cliente/cadastrar-cliente']);
  }

  consultarCliente(codigo: number) {
    this.router.navigate(['cliente/consultar-cliente', codigo]);
  }

  excluirCliente(codigo: number) {
    if (confirm("Deseja Excluir o Cliente?")) {
      this.ClienteService.excluirCliente(codigo).subscribe(data => {
        console.log(data);
        this.cliente = null;
        this.cpf = '';
      });
    }
  }

  limparDados(form?: NgForm) {
    if (form) {
      form.resetForm();
    }
    this.cpf = '';
    this.cliente = null;
    this.mensagemErro = '';
  }
}
<h2 class="text-center">Consultar Cliente</h2>
<div class="container">
    <form #clienteForm="ngForm" (ngSubmit)="consultarClientePorCPFCNPJ()">
        <div class="form-group">
            <label for="cpf">Cliente (CPF ou CNPJ)</label>
            <br>
            <input type="text"
                   class="form-control"
                   id="cpf"
                   [(ngModel)]="cpf"
                   name="cpf"
                   mask="000.000.000-00 || 00.000.000/0000-00"
                   [dropSpecialCharacters]="false"
                   required
                   style="width: 300px; display: inline-block; margin-right: 10px;">
        </div>
        <br>
        <button type="submit" class="btn btn-primary" style="margin-right: 10px;">Pesquisar</button>
        <button type="button" class="btn btn-secondary" (click)="limparDados(clienteForm)">Limpar</button>
    </form>
    <div *ngIf="mensagemErro" class="alert alert-danger">
        {{ mensagemErro }}
    </div>
    
    <br>

    <button *ngIf="!cliente" (click)="cadastrarCliente()" class="btn btn-info">Cadastrar Novo Cliente</button>
    <br>
    
    <table class="table table-striped" *ngIf="cliente">
        <thead>
            <tr>
                <th>CPF</th>
                <th>Nome</th>
            </tr>
        </thead>
        <tbody>
            <tr>
                <td>{{cliente.cpfcnpj}}</td>
                <td>{{cliente.nome}}</td>
                <td>
                    <button (click)="consultarCliente(cliente.id)" class="btn btn-primary" style="margin: 10px;">Visualizar</button>
                    <button (click)="alterarCliente(cliente.id)" class="btn btn-secondary" style="margin: 10px;">Alterar</button>
                    <button (click)="excluirCliente(cliente.id)" class="btn btn-danger" style="margin: 10px;">Excluir</button>
                </td>
            </tr>
        </tbody>
    </table>
</div>
